import React from "react";
import PersonDetails from "../PersonDetails/PersonDetails";
import "./PersonCard.css";

export default function PersonCard(props) {
  // expand the props object in order to not have to write
  // props.KEY every time I need a property
  const { person, handler, isActive, setRef } = props;

  //wrapper for the handler passed in the props
  //this is done in order to make sure the handler gets the correct
  //person
  const onClickHandler = () => {
    handler(person);
  };

  const trimPhrase = (phrase) => {
    return phrase.slice(0, 40) + "...";
  };

  //conditional className base on isActive property
  return (
    <div
      className={isActive ? "cardActive" : "card"}
      onClick={onClickHandler}
      ref={setRef}
    >
      <div
        style={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "space-between",
        }}
      >
        <div style={{ display: "flex", flexDirection: "column" }}>
          <span className="alignToStart">
            Name: {person.firstName} {person.lastName}
          </span>
          <span className="alignToStart">{person.email}</span>
        </div>
        <div>
          <img src={person.profilePicture} alt="nice one"></img>
        </div>
      </div>
      <p>{trimPhrase(person.info.about)}</p>
      <PersonDetails details={person.info} isActive={isActive}></PersonDetails>
    </div>
  );
}
